import React, { useEffect, useState } from 'react';
import axios from 'axios';

const CLIENT_ID = 'c4e4950088434c8d842394760ae0280a';
const REDIRECT_URI = 'localhost:3000/callback';
const AUTH_ENDPOINT = 'https://accounts.spotify.com/authorize';
const RESPONSE_TYPE = 'token';

function App() {
  const [token, setToken] = useState('');
  const [profile, setProfile] = useState(null);

  useEffect(() => {
    // Retrieve token from URL if redirected after login
    const hash = window.location.hash;
    if (hash) {
      const token = hash
        .substring(1)
        .split('&')
        .find(elem => elem.startsWith('access_token'))
        .split('=')[1];
      
      setToken(token);
      window.location.hash = '';  // Clear the hash
    }
  }, []);

  const logout = () => {
    setToken('');
    setProfile(null);
    window.localStorage.removeItem('token');
  };

  const fetchProfile = async () => {
    if (!token) return;
    try {
      const { data } = await axios.get('https://api.spotify.com/v1/me', {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setProfile(data);
    } catch (err) {
      console.error('Error fetching profile', err);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Spotify Profile Viewer</h1>
        {!token ? (
          <a
            href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}
          >
            Login to Spotify
          </a>
        ) : (
          <button onClick={logout}>Logout</button>
        )}
        {token && (
          <div>
            <button onClick={fetchProfile}>Fetch Profile</button>
            {profile && (
              <div>
                <h2>{profile.display_name}</h2>
                <p>Followers: {profile.followers.total}</p>
                <img src={profile.images[0]?.url} alt="Profile" width="100" />
              </div>
            )}
          </div>
        )}
      </header>
    </div>
  );
}

export const getTokenFromUrl = ()=>{
    return window.location.hash
    .substring(1)
    .split('&')
    .reduce((initial, item)=>{
    // #accessToken=mysecretkey&name=somerandomname
    let parts = item.split("=");
    initial[parts[0]] = decodeURIComponent(parts[1])
    
    return initial
    },{});
}

export default App;
